frappe.ui.form.on('Supplier', {
    onload: function (doc, dt, dn) {
        if (doc.is_new() == 0) {
            frappe.call({
                method: "empowery_co_op.api.get_linked_customer",
                args: {
                    suppname: cur_frm.doc.supplier_name,
                },
                callback: function (r) {
                    if (!r.message) {
                        const html = `There is no customer linked data for ` + cur_frm.doc.supplier_name
                        $(cur_frm.fields_dict.linked_customer_data.wrapper).html(html);
                    }
                    const linked_customer_data = r.message;
                    console.log(r.message)
                    console.log(linked_customer_data)
                    if (linked_customer_data) {
                        const html = `
                        <table class="table table-bordered">
                            <thead><tr><th>${__("#")}</th><th>${__("Type")}</th><th>${__("Customer Name")}</th><th>${__("Created Date")}</th></tr></thead>
                            <tbody>
                                ${linked_customer_data.map((c,i) => `<tr><td>${i+1}</td><td>${c.link_doctype}</td><td>${c.link_name}</td>
                                    <td>${c.join_date}</td></tr>`
                                ).join('')}
                            </tbody>
                        </table>`
                        $(cur_frm.fields_dict.linked_customer_data.wrapper).html(html);
                    }
                }
            });
        }
    },
    refresh: function (frm) {
        summary_length = 'Tag:< br>' + '&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;' + 'Cur length-' + frm.doc.offer_summary.length + ' Max-42'
        frm.set_df_property('offer_summary', 'description', summary_length);

        headline_length = 'Tags:{exp_dt},< br>' + '&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;' + 'Cur length-' + frm.doc.offer_headline.length + ' Max-50'
        frm.set_df_property('offer_headline', 'description', headline_length);

    }
});

frappe.ui.form.on('Customer', {
    onload: function (doc, dt, dn) {

        frappe.call({
            method: "empowery_co_op.api.get_linked_supplier",
            args: {
                custname: cur_frm.doc.customer_name,
            },
            callback: function (r) {
                if (!r.message) {
                    const html = `There is no supplier linked data for `+cur_frm.doc.customer_name
                    $(cur_frm.fields_dict.linked_supplier_data.wrapper).html(html);
                }
                const linked_supplier_data = r.message;
                console.log(r.message)
                console.log(linked_supplier_data)
                if (linked_supplier_data) {
                    const html = `
                        <table class="table table-bordered">
                            <thead><tr><th>${__("#")}</th><th>${__("Type")}</th><th>${__("Suppier Name")}</th><th>${__("Created Date")}</th></tr></thead>
                            <tbody>
                                ${linked_supplier_data.map((c,i) => `<tr><td>${i+1}</td><td>${c.parenttype}</td><td>${c.parent}</td>
                                    <td>${c.join_date}</td></tr>`
                                ).join('')}
                            </tbody>
                        </table>`
                    $(cur_frm.fields_dict.linked_supplier_data.wrapper).html(html);
                }
            }
        });
    }
});

frappe.ui.form.on("Supplier Engagement Report Settings", "yellow_zone_start_value", function (frm) {
    cur_frm.set_value("red_zone_end_value", frm.doc.yellow_zone_start_value);

    frm.doc.yellow_zone_start_value!=null?(val1=parseFloat(frm.doc.yellow_zone_start_value.toPrecision(2))+0.1):(val1='not set');
    frm.doc.yellow_zone_end_value!=null?(val2=parseFloat(frm.doc.yellow_zone_end_value.toPrecision(2))+0.0):(val2='not set');
    cur_frm.set_value("note", "Color 2 will be shown between "+val1 +" and "+val2);


});


